mapper.select:
SELECT
FROM product p LEFT JOIN product_category pc ON p.category_id = pc.id WHERE p.id IN
#{id}

SELECT

FROM product p
LEFT JOIN product_category pc ON p.category_id = pc.id


        AND p.name LIKE CONCAT('%', #{name}, '%')


        AND p.category_id = #{categoryId}


        AND p.price >= #{minPrice}


        AND p.price <= #{maxPrice}


        AND p.status = #{status}


ORDER BY p.create_time DESC
LIMIT #{offset}, #{limit}

SELECT COUNT(*)
FROM product p


        AND p.name LIKE CONCAT('%', #{name}, '%')


        AND p.category_id = #{categoryId}


        AND p.price >= #{minPrice}


        AND p.price <= #{maxPrice}


        AND p.status = #{status}

mapper.insert:
INSERT INTO product (
    name, category_id, price, original_price, stock, sales, description,
    images, specifications, status, h5_link
) VALUES (
    #{name}, #{categoryId}, #{price}, #{originalPrice}, #{stock}, #{sales}, #{description},
    #{images, typeHandler=com.travel.mapper.typehandler.JsonListTypeHandler},
    #{specifications, typeHandler=com.travel.mapper.typehandler.JsonObjectTypeHandler},
    #{status}, #{h5Link}
)

mapper.update:
UPDATE product
name = #{name}, category_id = #{categoryId}, price = #{price}, original_price = #{originalPrice}, stock = #{stock}, sales = #{sales}, description = #{description}, images = #{images, typeHandler=com.travel.mapper.typehandler.JsonListTypeHandler}, specifications = #{specifications, typeHandler=com.travel.mapper.typehandler.JsonObjectTypeHandler}, status = #{status}, h5_link = #{h5Link},
WHERE id = #{id}

mapper.select:
SELECT COUNT(*)
FROM order_item
WHERE item_type = 'PRODUCT' AND item_id = #{productId}

mapper.update:
UPDATE product
SET stock = #{stock}
WHERE id = #{id}

UPDATE product
SET sales = sales + #{quantity}
WHERE id = #{id}
